    lv_obj_set_style_text_color(ui_LabelReplyContent, lv_color_hex(0xFFFFFF), LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_opa(ui_LabelReplyContent, 255, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_letter_space(ui_LabelReplyContent, 0, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_line_space(ui_LabelReplyContent, 5, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_align(ui_LabelReplyContent, LV_TEXT_ALIGN_LEFT, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_font(ui_LabelReplyContent, &ui_font_KaiTiCN20, LV_PART_MAIN | LV_STATE_DEFAULT);

    // Removed unused ImageReplyLogo object

    ui_LabelListenSpeak = lv_label_create(ui_ScreenListen);
    lv_obj_set_width(ui_LabelListenSpeak, 260);  // Width for better text display
    lv_obj_set_height(ui_LabelListenSpeak, LV_SIZE_CONTENT);
    lv_obj_set_x(ui_LabelListenSpeak, 0);
    lv_obj_set_y(ui_LabelListenSpeak, -20);  // Move a bit further down
    lv_obj_set_align(ui_LabelListenSpeak, LV_ALIGN_BOTTOM_MID);
    // Show full STT/response text with wrapping (no truncation)
    lv_label_set_long_mode(ui_LabelListenSpeak, LV_LABEL_LONG_WRAP);
    lv_label_set_text(ui_LabelListenSpeak, "");
    lv_obj_add_flag(ui_LabelListenSpeak, LV_OBJ_FLAG_HIDDEN);     /// Flags
