    lv_obj_set_style_text_opa(ui_LabelListenSpeak, 255, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_align(ui_LabelListenSpeak, LV_TEXT_ALIGN_CENTER, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_text_font(ui_LabelListenSpeak, &ui_font_KaiTiCN20, LV_PART_MAIN | LV_STATE_DEFAULT);
    // Remove all border and background styling - text only
    lv_obj_set_style_bg_opa(ui_LabelListenSpeak, 0, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_border_width(ui_LabelListenSpeak, 0, LV_PART_MAIN | LV_STATE_DEFAULT);
    lv_obj_set_style_pad_all(ui_LabelListenSpeak, 0, LV_PART_MAIN | LV_STATE_DEFAULT);

    lv_obj_add_event_cb(ui_PanelSleep, ui_event_PanelSleep, LV_EVENT_ALL, NULL);

